import React, { useRef } from "react";
import AvatarReact from "react-avatar";
import "./Avatar.css";


function Avatar() {
  const {menuRef} = useRef();
  return (
    <div>
      <button className="avatar-button" type="button" onClick={()=>setOpen(!open)}>
        <AvatarReact size="40" round={true} name="Deependra" />
        &#x25BC;
      </button>
      <div className="avatar-dropdow">
        <div className="profile-details">
           <p>Hello</p>
           <h3>Deependra</h3>
           <h6>View and Edit profile</h6>
        </div>
        <div className="divider"></div>
        <div className="dropdown-items">
          <div>
            Start Selling
          </div>
          <div>
            Favorites
          </div>
          <div>
            Logout
          </div>
        </div>
        
      </div>
    </div>
  );
}

export default Avatar;
