import React, { useEffect, useRef, useState } from "react";
import AvatarReact from "react-avatar";
import "./Avatar.css";

function Avatar() {
  const { menuRef } = useRef();
  const [open, setOpen] = useState(false);

  useEffect(()=>{
      function handleOutSideClick(e) {
        if(open && menuRef.current && !menuRef.current.contains(e.target)){
          setOpen(false);
        }
      }
      document.addEventListener('click',handleOutSideClick)
      return ()=>{
        document.removeEventListener('click',handleOutSideClick)
      }
  },[open])

  return (
    <div className="avatar">
      <button
        className="avatar-button"
        type="button"
        onClick={() => setOpen(!open)}
      >
        <AvatarReact size="40" round={true} name="Deependra" />
        &#x25BC;
      </button>
      <div
        ref={menuRef}
        className={
          open ? "avatar-dropdown avatar-drop-active" : "avatar-dropdown"
        }
      >
        <div className="avatar-dropdow">
          <div className="avatar-profile">
          <AvatarReact
                    className="avatar-react-big"
                    size="60"
                    round={true}
                    name='Deependra'
                  />
          </div>
          <div className="profile-details">
            <p>Hello</p>
            <h3>Deependra</h3>
            <h6>View and Edit profile</h6>
          </div>
          <div className="divider"></div>
          <div className="dropdown-items">
            <div>Start Selling</div>
            <div>Favorites</div>
            <div>Logout</div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Avatar;
